CmdAutoDrive::CmdAutoDrive(double Power, double Direction, double Duration): Command() {
    m_Power = Power;
    m_Direction = Direction;
    m_Duration = Duration;

    //Set PID values, *-1 when going backwards
    if( m_Power > 0 )
    {
        p_constant = +5.0;
        i_constant = +0.0;
        d_constant = +10.0;
    }
    else
    {
        p_constant = -5.0;
        i_constant = -0.0;
        d_constant = -10.0;
    }

    //Set up PID controller
    DriveController = new PIDController(p_constant, i_constant, d_constant,static_cast<PIDSource*>( new GyroPIDSource()), new DrivePIDOutput(m_Power));
    DriveController->SetContinuous(true);
    DriveController->SetInputRange(-180,+180);
    DriveController->SetOutputRange(-1,+1);
    DriveController->SetSetpoint(Direction);

        // Use requires() here to declare subsystem dependencies
    // eg. requires(Robot::chassis.get());
    // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
	Requires(Robot::subDriveBase.get());
    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
}

